
// | This module provides support for the
// | PureScript interactive mode, PSCI.
"use strict";
var Control_Bind = require("../Control.Bind/index.js");
var Effect = require("../Effect/index.js");
var Effect_Console = require("../Effect.Console/index.js");
var Prelude = require("../Prelude/index.js");

// | The `Eval` class captures those types which can be
// | evaluated in the REPL.
// |
// | There are instances provided for the `Effect` type
// | constructor and any `Show`able types.
var Eval = function ($$eval) {
    this["eval"] = $$eval;
};
var evalShow = function (dictShow) {
    return new Eval(Effect_Console.logShow(dictShow));
};
var $$eval = function (dict) {
    return dict["eval"];
};
var evalEffect = function (dictEval) {
    return new Eval(function (x) {
        return Control_Bind.bind(Effect.bindEffect)(x)($$eval(dictEval));
    });
};
module.exports = {
    "eval": $$eval,
    Eval: Eval,
    evalEffect: evalEffect,
    evalShow: evalShow
};
